import CartActionTypes from './cart.types';

const INTITIAL_STATE = {
    hidden: true,
    cartItems: []
};

const cartReducer = (state = INTITIAL_STATE, action) => {
    switch(action.type) {
        case CartActionTypes.TOGGLE_CART_HIDDEN:
            return {
                ...state,
                hidden: !state.hidden
            };
            default:
                return state; 
    }
};

export default cartReducer;
